nums = CodeHelpers.shitmorsetonumbers(ciphertext)

alphabet = 'abcdefghijklmnopqrstuvwxyz'
plaintext = []
nums.each do |n|
  if n < 26
    plaintext << alphabet[n].upcase
  elsif n >= 26 && n <= 54
    plaintext << alphabet[n-26]
  elsif n == 62
    plaintext << " "
  elsif n == 63
    plaintext << "."
  end
end
puts plaintext.join('')